package loops.sgm;

public class Switch_case {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String day = "sunday";
		
		switch(day)
		{
		case "sunday": System.out.println("Cricket");
		              break;
		case "Monday": System.out.println("kabbadi");
		              break;
		case "tuesday": System.out.println("kho-kho");
		               break;
		case "wednsday": System.out.println("Football");
		               break;
		case "thursday": System.out.println("badminton");
		                break;
		case "friday": System.out.println("basketball");
		               break;
		case "saturday": System.out.println("running");
	                     break;
		 
		}
	}

}
